import { Route, RouterProvider } from "react-router-dom"
import HomePage from "./pages/home";
import ReportPage from "./pages/report";
import FindPage from "./pages/find";
import AboutPage from "./pages/about";
import { useState, useEffect } from "react";
import { db } from './firebase-config'
import { collection, getDocs } from 'firebase/firestore'
import { Link, createBrowserRouter, createRoutesFromElements } from "react-router-dom"

const router = createBrowserRouter(
  createRoutesFromElements(
    <Route path="/" element={<Navbar />}>
      <Route index element={<HomePage />} />
      <Route path="report" element={<ReportPage />} />
      <Route path="find" element={<FindPage />} />
      <Route path="about" element={<AboutPage />} />
    </Route>
  )
)

function App() {

  const [table, setTableState] = useState()
  const table_collection_ref = collection(db, "example")
  useEffect(() => {
    const getRecords = async () => {
      const data = await getDocs(table_collection_ref);
      setTableState(data.docs.map((doc) => ({...doc.data(), id: doc.id })))
    }

    getRecords()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  console.log("From app")
  console.log(table)
  return (
    <div className="App">
      <header className="App-header">
      <RouterProvider router={router} />
      </header>
    </div>
  );
}

export default App;
