import stevenslogo from '../images/StevensLogo.png'
import { signOut } from 'firebase/auth';
import { fb_auth } from '../firebase-config';
import './home.css'

export default function HomePage (props) {
    // Log out of Admin
    const logOut = async() => {
        await signOut(fb_auth)
    }
    //
    // Database
    if (props.authed && props.database) {
        console.log(props.database)
        let table = document.createElement('div')
        let list = document.getElementById("view-requests")
        let headers = ["Request ID", "First Name:", "Last Name:", "Email:"]
        list.innerHTML = ""
        props.database.map((record) => {
            console.log(record.fname)
            let li = document.createElement("li")
            li.innerText = "First Name: " + record.fname + " Last Name: " + record.lname + " Email" 
            list.appendChild(li)
        })
    }
    //

    if (props.authed) {
        return (
            <div>
                <div className="login-desc">
                    <p>Logged in as: {props.authed?.email}</p>
                    <button onClick={logOut}>Sign Out</button>
                </div>
                <h1>View Requests</h1>
                <br></br>
                <div className='view'>
                    <ul id="view-requests"></ul>
                </div>
            </div>
        )
    } else {
        return (
            <div>
                <h1>Welcome to the Stevens Lost & Found Website!</h1>
                <br></br>
                <br></br>
                <br></br>
                <br></br>
                <img src={stevenslogo} alt="logo" className='stevenslogo'/>
            </div>
        )
    }
}
