import stevenslogo from '../images/StevensLogo.png'
import { useState, useEffect } from "react";
import { fb_db, fb_auth } from '../firebase-config'
import { collection, getDocs } from 'firebase/firestore'
import { onAuthStateChanged, signInWithEmailAndPassword, signOut } from 'firebase/auth';

export default function HomePage () {
    // Data base
    const [table, setTableState] = useState()
    const table_collection_ref = collection(fb_db, "example")
    const getRecords = async () => {
        const data = await getDocs(table_collection_ref);
        setTableState(data.docs.map((doc) => ({...doc.data(), id: doc.id })))
    }
    //

    // Authentication
    useEffect(() => {
        onAuthStateChanged(fb_auth, (currentUser) => {
            setUser(currentUser)
        })
    }, [])
    const [loggedInUser, setUser] = useState("")
    const [loginEmail, setLoginEmail] = useState("")
    const [loginPassword, setloginPassword] = useState("")
    const logIn = async () => {
        try {
            const user = await signInWithEmailAndPassword(
                fb_auth,
                loginEmail,
                loginPassword
            )
            console.log(user)
        } catch (err) {
            console.log(err.message)
        }
        onAuthStateChanged(fb_auth, (currentUser) => {
            setUser(currentUser)
        })
    }
    const logOut = async() => {
        await signOut(fb_auth)
    }

    //
    let userType ="student"
    let itemslost = 10
    let openrequests = 2
    let closedrequests = 5

    if (userType === "admin") {
        return (
            <div>
                <h1>Analytics</h1>
                <br></br>
                <div className='fastfacts'>
                    <ul>
                    <li>Items in Lost & Found: {itemslost} </li>
                    <li>Open requests: {openrequests}</li>
                    <li>Closed requests: {closedrequests}</li>
                    </ul>
                </div>
            </div>
        )
    } else {
        return (
            <div>
                <h4>Sign in to continue</h4>
                <br></br>
                <p>Username:</p>
                <input 
                    onChange={(e) => {
                        setLoginEmail(e.target.value)
                    }}
                />
                <p>Password:</p>
                <input 
                    onChange={(e) => {
                        setloginPassword(e.target.value)
                    }}
                />
                <button onClick={logIn}>Log in</button>
                <p>User: {loggedInUser?.email} Password: {loginPassword} </p>
                <img src={stevenslogo} alt="logo" className='stevenslogo'/>
            </div>
        )
    }
}
