import stevenslogo from '../images/StevensLogo.png'
import { useState, useEffect, useRef } from "react";
import { db } from '../firebase-config'
import { collection, getDocs } from 'firebase/firestore'

export default function HomePage () {
    // Data base
    const [table, setTableState] = useState()
    const table_collection_ref = collection(db, "example")
    useEffect(() => {
      const getRecords = async () => {
        const data = await getDocs(table_collection_ref);
        setTableState(data.docs.map((doc) => ({...doc.data(), id: doc.id })))
      }
  
      getRecords()
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])
  
    console.log(table)
    //

    // Log in
    function logIn(user, pass) {
        const [table, setTableState] = useState()
        const table_collection_ref = collection(db, "example")
        useEffect(() => {
          const getRecords = async () => {
            const data = await getDocs(table_collection_ref);
            setTableState(data.docs.map((doc) => ({...doc.data(), id: doc.id })))
          }
      
          getRecords()
          // eslint-disable-next-line react-hooks/exhaustive-deps
        }, [])
    }
    function logOut() {

    }
    const userRef = useRef(null);
    const passRef = useRef(null);
    let username = ""
    let password = ""
    const handleAuth = () => {
        username = userRef.current.value
        password = passRef.current.value
        logIn(username, password)
    }

    //


    let userType ="student"
    let itemslost = 10
    let openrequests = 2
    let closedrequests = 5

    if (userType === "admin") {
        return (
            <div>
                <h1>Analytics</h1>
                <br></br>
                <div className='fastfacts'>
                    <ul>
                    <li>Items in Lost & Found: {itemslost} </li>
                    <li>Open requests: {openrequests}</li>
                    <li>Closed requests: {closedrequests}</li>
                    </ul>
                </div>
            </div>
        )
    } else {
        return (
            <div>
                <h4>Sign in to continue</h4>
                <br></br>
                <p>Username:</p>
                <input 
                    type="text"
                    ref={userRef}
                />
                <p>Password:</p>
                <input 
                    type="text" 
                    ref={passRef}
                />
                <button onClick={handleAuth}>Log in</button>
                <p>User: {username} Password: {password} </p>
                <img src={stevenslogo} alt="logo" className='stevenslogo'/>
            </div>
        )
    }
}
