import { NavLink, Outlet } from "react-router-dom";
import './navbar.css'
import { signOut } from 'firebase/auth';
import { fb_auth } from '../firebase-config';

export default function Navbar(props) {
    console.log(props.authed)
    if(props.authed === null) {
        var login_desc = document.getElementsByClassName('login-desc')
        login_desc.display = 'none'
    } else {
        var login_desc = document.getElementsByClassName('login-desc')
        login_desc.display = 'inline'
    }
    // Log out of Admin
    const logOut = async() => {
        await signOut(fb_auth)
    }
    //
    return (
        <div>
        <nav className="nav">
        <label className="sitename">Stevens Lost & Found Tool</label>
        <ul>
          <li><NavLink className="navb" to="/">Home</NavLink></li>
          <li><NavLink className="navb" to="/report">Report Lost Item</NavLink></li>
          <li><NavLink className="navb" to="/find">Find An Item</NavLink></li>
          <li><NavLink className="navb" to="/about">About</NavLink></li>
        </ul>
        </nav>
        <div className="login-desc">
            <p>Logged in as: {props.authed?.email}</p>
            <button onClick={logOut}>Sign Out</button>
        </div>
        <div className="container">
            <Outlet />
        </div>
        </div>
    )
}
